import axios from "axios";
import { useContext,useState } from "react"
import { Link, Navigate } from "react-router-dom"
import { UserContext } from "../UserContext";

export default function LoginPage(){
    const [email,setEmail]=useState('')
    const [password,setPassword]=useState('')
    const [redirect,setRedirect]=useState(false)
    const {setUser}=useContext(UserContext)
    async function loginUser(ev){
        ev.preventDefault();
        try{            
        const userInfo= await axios.post('/login',{email,password})
        setRedirect(true)
        }
        catch(e){
            alert('Login failed')
        }

        
    }
    if(redirect){
        return  <Navigate to={'/'}/>
    }

    return(
        <div className="mt-4 grow flex items-center justify-around">
            <div className="mb-64">
            <h1 className="text-4xl text-center mb-4">Login</h1>
            <form className="max-w-md mx-auto " onSubmit={loginUser}>
                <input type="email" placeholder='your@email.com' onChange={ev=>setEmail(ev.target.value)}/>
                <input type="password" placeholder="password" onChange={(ev)=>{setPassword(ev.target.value)}}></input>
                <button className="primary">Login</button>
                <div className="text-center py-2 text-gray-500">
                    Dont't have an account yet? <Link className="underline text-black" to={'/register'}>Register now</Link>
                </div>
            </form>
            </div>
        </div>
    )
}
