import { UserContext } from "../UserContext"
import { useContext, useState } from "react"
import {Navigate,Link, useParams} from "react-router-dom"
import axios from "axios";
import PlacesPage from "./PlacesPages";

export default function AccountPage(){
    const [redirect,setRedirect]=useState(null)
    const {subpage}=useParams();
    const {ready,user,setUser}=useContext(UserContext);
    
    async function logout(){
        await axios.post('/logout')
        setRedirect('/')
        setUser(null)
    }

    if(!ready){
        return(
            <div>Loading...</div>
        )
    }
    
    if(ready && !user && !redirect){
        return <Navigate to={'/login'}/>
    }

    function linkClasses(type=null){
        let classes = 'py-2 px-6 font-bold'
        if(type===subpage || (subpage===undefined && type==='profile')){
            classes +=' bg-primary text-white rounded-full'
        }
        return classes
    }   

    if(redirect){
        return <Navigate to={redirect}/>
    }

    return(
        <div>
            <nav className="w-full flex mt-8 gap-2 justify-center">
                <Link className={linkClasses('profile')} to={'/account'}>My Profile</Link>
                <Link className={linkClasses('bookings')} to={'/account/bookings'}>My Booking</Link>
                <Link className={linkClasses('places')} to={'/account/places'}>My accomodations</Link>
            </nav>
            {subpage === undefined && (
                <div className="text-center max-w-lg mx-auto">
                    Logged in as {user.name} ({user.email})<br/>
                    <button className="primary max-w-sm mt-2" onClick={logout}>Log out</button>
                </div>
            )}
            {subpage === 'places' && (
                <PlacesPage/>
            )}
        </div>
    )
}
