import { UserContext } from "../UserContext"
import { useContext } from "react"
import {Navigate,Link, useParams} from "react-router-dom"

export default function AccountPage(){
    const {subpage}=useParams();
    const {ready,user}=useContext(UserContext);
    if(subpage===undefined){
        subpage='profile';
    }

    if(!ready){
        return(
            <div>Loading...</div>
        )
    }
    
    if(ready && !user){
        return <Navigate to={'/login'}/>
    }

    function linkClasses(type=null){
        let classes = 'py-2 px-6 font-bold'
        if(type===subpage || (subpage===undefined && type==='profile')){
            classes +=' bg-primary text-white rounded-full'
        }
        return classes
    }   

    return(
        <div>
            <nav className="w-full flex mt-8 gap-2 justify-center">
                <Link className={linkClasses('profile')} to={'/account'}>My Profile</Link>
                <Link className={linkClasses('bookings')} to={'/account/bookings'}>My Booking</Link>
                <Link className={linkClasses('places')} to={'/account/places'}>My accomodations</Link>
            </nav>
            {subpage === 'profile' && (
                <div className="text-center max-w-xl mx-auto">
                    Logged in as {user.name} ({user.email})<br/>
                    <button className="primary max-w-sm mt-2"></button>
                </div>
            )}
        </div>
    )
}
