import axios from "axios";
import { useEffect, useState } from "react";
import { Link, useParams } from "react-router-dom"

export default function PlacePage(){
    const {id}=useParams();
    const [place,setPlace]=useState({})
    useEffect(()=>{
        console.log("Hi")
        if(!id){
            return;
        }
        axios.get(`/places/${id}`,).then(response=>{
            setPlace(response.data)
        })
    },[id])
    return(
        <div className="mt-4 bg-gray-100 -mx-8 px-8 py-8">
            <h1 className="text-3xl">{place.title}</h1>
            <a className="my-2  block font-semibold underline" target='_blank' href={'https://maps.google.com/?q='+place.address}>{place.address}</a>
            <div className="grid gap-2 grid-cols-[2fr_1fr]"></div>
            <div>
                {place.photos?.[0] && (
                    <img src={'http://localhost:    /uploads/'+place.photos[0]} />
                )}
            </div>
        </div>
    )
}
