import axios from "axios";
import { differenceInCalendarDays, format } from "date-fns";
import { useEffect, useState } from "react";
import AccountNav from "../AccountNav";
import PlaceImg from "../PlaceImg";

export default function BookingsPage(){
    const [bookings,setBookings]=useState([])
    useEffect(()=>{
        axios.get('/bookings').then(response=>{
            setBookings(response.data)
            console.log(bookings)
        })
    },[])
    return(
       <div>
         <AccountNav/>  
         <div>
            
            {bookings?.length>0 && bookings.map(booking=>(
                <div className="flex gap-4 bg-gray-200 rounded-2xl overflow-hidden">
                    <div className="w-48">
                        <PlaceImg place={booking.place} />
                        </div>
                        <div className="py-3 grow pr-3">
                            <h2 className="text-xl">{booking.place.title}</h2>
                            <div className="border-t border-gray-300">
                            {format(new Date(booking.checkIn),'dd-MM-yyyy')} -- {format(new Date(booking.checkIn),'dd-MM-yyyy')}
                                </div>
                                <div>
                                    Number of nights: {differenceInCalendarDays(new Date(booking.checkOut),new Date(booking.checkIn))}<br/>
                                    </div>
                                    <div>
                                    Total Price: ${booking.price}
                                    </div>
                        </div>
                </div> 
            ))}
         </div>
       </div> 
    )
}
